var playerOne = 500; 
var playerTwo = 600;
var highScore;
if ( playerOne &gt; playerTwo  ) {
    highScore = playerOne; 
} else {
    highScore = playerTwo; 
}

can be rewritten as Ternary Operator

var highScore = (playerOne &gt; playerTwo) ? playerOne : playerTwo; 
