	amount = amount + 100;
	//increment index 
	i++;
}

alert(amount);
</pre><br><h2> while loop - increment n and add to x<div style="font-size: 0.5em; color: blue;"><a class="dcTOCTop" title="Go to top of tiddler" href="javascript:;"> [top]</a></div></h2><pre>var n = 0;
