  { FirstName: 'Ashley', LastName: 'Yards' },
  { FirstName: 'Martin', LastName: 'Stove' },
  { FirstName: 'Robert', LastName: 'Jones' },
  { FirstName: 'Erika', LastName: 'Johnson'  },
  { FirstName: 'Melissa', LastName: 'Banks' }
];
items.sort(function (a, b) {
  if (a.LastName &gt; b.LastName) {
    return 1;
  }
  if (a.LastName &lt; b.LastName) {
    return -1;
  }
  // a must be equal to b
  return 0;
});
</pre><br><h3> string return primitive value<div style="font-size: 0.5em; color: blue;"><a class="dcTOCTop" title="Go to top of tiddler" href="javascript:;"> [top]</a></div></h3><pre>var x = new String('Hello world');
